using System;

namespace Zongsoft.Externals.Aliyun
{
	/// <summary>
	/// 表示阿里云访问凭证的配置项类。
	/// </summary>
	public interface ICertificate
	{
		string Name
		{
			get; set;
		}

		/// <summary>
		/// 获取凭证的标识（即：AccessKey）。
		/// </summary>
		string Code
		{
			get; set;
		}

		/// <summary>
		/// 获取凭证的密钥（即：AccessKeySecret）。
		/// </summary>
		string Secret
		{
			get; set;
		}
	}
}
